public class ApprentissageDBAdapter {

public static final String TABLE_REMOTE = "remote";
public static final String COLUMN_ID = "id";
public static final String COLUMN_NOM = "nom";
public static final String COLUMN_ONOFF = "OnOff";
public static final String COLUMN_VOL_PLUS = "Vol_plus";
public static final String COLUMN_VOL_MOINS = "Vol_moins";
public static final String COLUMN_PROGRAMME_PLUS = "Programme_plus";
public static final String COLUMN_PROGRAMME_MOINS = "Programme_moins";

private static final String TAG = "ApprentissageDBAdapter";
private ApprentissageDBAdapter mDbHelper;
private SQLiteDatabase mDb;

private static final String DATABASE_NAME = "remote.db";
private static final int DATABASE_VERSION = 1;

private final Context mCtx;

// Commande sql pour la création de la base de données
private static final String DATABASE_CREATE = "create table "
        + TABLE_REMOTE + "(" + COLUMN_ID
        + " integer primary key autoincrement, " + COLUMN_NOM + " TEXT, " + COLUMN_ONOFF + " TEXT, "
        + COLUMN_VOL_PLUS + " TEXT, " + COLUMN_VOL_MOINS + " TEXT, " + COLUMN_PROGRAMME_PLUS + " TEXT, " + COLUMN_PROGRAMME_MOINS + "TEXT);";




private static class Apprentissage extends SQLiteOpenHelper {

    Apprentissage(Context context) {
        super(context, DATABASE_NAME, null, DATABASE_VERSION);
    }

    @Override
    public void onCreate(SQLiteDatabase db) {
        Log.w(TAG, DATABASE_CREATE);
        db.execSQL(DATABASE_CREATE);
    }

    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        Log.w(Apprentissage.class.getName(),
                "Upgrading database from version " + oldVersion + " to "
                        + newVersion + ", which will destroy all old data");
        db.execSQL("DROP TABLE IF EXISTS " + TABLE_REMOTE);
        onCreate(db);
    }
}



public ApprentissageDBAdapter(Context ctx) {
    this.mCtx = ctx;
}

public ApprentissageDBAdapter open() throws SQLException {
    mDbHelper = new ApprentissageDBAdapter(mCtx);
    mDb = mDbHelper.***getWritableDatabase(); // Error is here !
    return this;
}

public void close() {
    if (mDbHelper != null) {
        mDbHelper.close();
    }
}

public long createApprentissage(String ID, String Nom, String OnOff, String Vol_plus, String Vol_moins,
                                String Programme_plus, String Programme_moins) {

    ContentValues initialValues = new ContentValues();
    initialValues.put(COLUMN_ID, ID);
    initialValues.put(COLUMN_NOM, Nom);
    initialValues.put(COLUMN_ONOFF, OnOff);
    initialValues.put(COLUMN_VOL_PLUS, Vol_plus);
    initialValues.put(COLUMN_VOL_MOINS, Vol_moins);
    initialValues.put(COLUMN_PROGRAMME_PLUS, Programme_plus);
    initialValues.put(COLUMN_PROGRAMME_MOINS, Programme_moins);

    return mDb.insert(TABLE_REMOTE, null, initialValues);
}

public boolean deleteAllApprentissage() {

    int doneDelete = 0;
    doneDelete = mDb.delete(TABLE_REMOTE, null, null);
    Log.w(TAG, Integer.toString(doneDelete));
    return doneDelete &gt; 0;

}

public Cursor fetchApprentissageByName(String inputText) throws SQLException {
    Log.w(TAG, inputText);
    Cursor mCursor = null;
    if (inputText == null || inputText.length() == 0) {
        mCursor = mDb.query(TABLE_REMOTE, new String[]{COLUMN_ID, COLUMN_NOM,
                        COLUMN_ONOFF, COLUMN_VOL_PLUS, COLUMN_VOL_MOINS, COLUMN_PROGRAMME_PLUS, COLUMN_PROGRAMME_MOINS},
                null, null, null, null, null);

    } else {
        mCursor = mDb.query(true, TABLE_REMOTE, new String[]{COLUMN_ID, COLUMN_NOM,
                        COLUMN_ONOFF, COLUMN_VOL_PLUS, COLUMN_VOL_MOINS, COLUMN_PROGRAMME_PLUS, COLUMN_PROGRAMME_MOINS},
                COLUMN_NOM + " like '%" + inputText + "%'", null,
                null, null, null, null);
    }
    if (mCursor != null) {
        mCursor.moveToFirst();
    }
    return mCursor;

}

public Cursor fetchAllApprentissage() {

    Cursor mCursor = mDb.query(TABLE_REMOTE, new String[]{COLUMN_ID, COLUMN_NOM,
                    COLUMN_ONOFF, COLUMN_VOL_PLUS, COLUMN_VOL_MOINS, COLUMN_PROGRAMME_PLUS, COLUMN_PROGRAMME_MOINS},
            null, null, null, null, null);

    if (mCursor != null) {
        mCursor.moveToFirst();
    }
    return mCursor;
}

