import android.view.View;
import android.widget.AdapterView;
import android.widget.EditText;
import android.widget.FilterQueryProvider;
import android.widget.ListView;
import android.widget.SimpleCursorAdapter;
import android.widget.Toast;

public class MainActivity extends AppCompatActivity {

private ApprentissageDBAdapter dbHelper;
private SimpleCursorAdapter dataAdapter;

@Override
public void onCreate(Bundle savedInstanceState) {
    super.onCreate(savedInstanceState);
    setContentView(R.layout.activity_main);

    dbHelper = new ApprentissageDBAdapter(this);
    dbHelper.open();
    //Clean all data
    dbHelper.deleteAllApprentissage();

    //Add some data
    dbHelper.insertSomeApprentisage();

    //Generate ListView from SQLite Database
    displayListView();

}

private void displayListView() {


    Cursor cursor = dbHelper.fetchAllApprentissage();

    // The desired columns to be bound
    String[] columns = new String[] {
            ApprentissageDBAdapter.COLUMN_ID,
            ApprentissageDBAdapter.COLUMN_NOM,
            ApprentissageDBAdapter.COLUMN_ONOFF,
            ApprentissageDBAdapter.COLUMN_VOL_PLUS,
            ApprentissageDBAdapter.COLUMN_VOL_MOINS,
            ApprentissageDBAdapter.COLUMN_PROGRAMME_PLUS,
            ApprentissageDBAdapter.COLUMN_PROGRAMME_MOINS
    };

    // the XML defined views which the data will be bound to
    int[] to = new int[] {
            R.id.ID,
            R.id.Nom,
            R.id.OnOff,
            R.id.Volume_plus,
            R.id.Volume_moins,
            R.id.Progr_plus,
            R.id.Progr_moins,
    };

    // create the adapter using the cursor pointing to the desired data
    //as well as the layout information
    dataAdapter = new SimpleCursorAdapter(
            this, R.layout.remote_info,
            cursor,
            columns,
            to,
            0);

    ListView listView = (ListView) findViewById(R.id.listView);
    // Assign adapter to ListView
    listView.setAdapter(dataAdapter);


    listView.setOnItemClickListener(new AdapterView.OnItemClickListener() {
        @Override
        public void onItemClick(AdapterView&lt;?&gt; listView, View view,
                                int position, long id) {
            // Get the cursor, positioned to the corresponding row in the result set
            Cursor cursor = (Cursor) listView.getItemAtPosition(position);

            // Get the state's capital from this row in the database.
            String Nom =
                    cursor.getString(cursor.getColumnIndexOrThrow("nom"));
            Toast.makeText(getApplicationContext(),
                    Nom, Toast.LENGTH_SHORT).show();

        }
    });

    EditText myFilter = (EditText) findViewById(R.id.myFilter);
    myFilter.addTextChangedListener(new TextWatcher() {

        public void afterTextChanged(Editable s) {
        }

        public void beforeTextChanged(CharSequence s, int start,
                                      int count, int after) {
        }

        public void onTextChanged(CharSequence s, int start,
                                  int before, int count) {
            dataAdapter.getFilter().filter(s.toString());
        }
    });

    dataAdapter.setFilterQueryProvider(new FilterQueryProvider() {
        public Cursor runQuery(CharSequence constraint) {
            return dbHelper.fetchApprentissageByName(constraint.toString());
        }
    });

}


public boolean onOptionsItemSelected(MenuItem item)
    {
        switch (item.getItemId())
        {
          /*  case R.id.menu_connect:
                Intent newIntent = new Intent(this, DeviceScanActivity.class);
                startActivityForResult(newIntent, REQUEST_SELECT_DEVICE);
              return true; */
            case R.id.menu_settings:
                Intent iSetting = new Intent(this, Parametres.class);
                startActivity(iSetting);
                return true;
            default:
                return super.onOptionsItemSelected(item);
        }
    }

@Override
public boolean onCreateOptionsMenu(Menu menu)
{

    getMenuInflater().inflate(R.menu.activity_main, menu);
    return true;
}


}



Apprentissage.java


package colbert.telecommandeergonomique;

/**
 * Created by miwaky on 10/06/2016.
 */

public class Apprentissage {
    private long id;
    private String Nom;
    private String OnOff;
    private String Vol_plus;
    private String Vol_moins;
    private String Programme_plus;
    private String Programme_moins;


    public long getId() {
        return id;
    }

    public void setId(long id) {
        this.id = id;
    }

    public String getNom() {
        return Nom;
    }

    public void setNom(String Nom) {
        this.Nom = Nom;}


    public String getOnOff() {
        return OnOff;
    }

    public void setOnOff(String OnOff) {
        this.OnOff = OnOff;
    }


    public void setVol_plus(String Vol_plus) {
        this.Vol_plus = Vol_plus;
    }

    public String getVol_plus() {
        return Vol_plus;
    }

    public void setVol_moins(String Vol_moins) {
        this.Vol_moins = Vol_moins;
    }

    public String getVol_moins() {
        return Vol_moins;
    }


public void setProgramme_moins(String Programme_moins) {
    this.Programme_moins = Programme_moins;
}

public String getProgramme_moins() {
    return Programme_moins;
}

public void setProgramme_plus(String Programme_plus) {
    this.Programme_plus = Programme_plus;
}

public String getProgramme_plus() {
    return Programme_plus;
}


    // Sera utilisée par ArrayAdapter dans la ListView
    @Override
    public String toString() {
        return Nom ;
    }
}
